you explore it?\r\n  |\r\n  */\r\n  use AuthenticatesAndRegistersUsers, ThrottlesLogins;\r\n  /**\r\n   * Where to redirect users after login / registration.\r\n   *\r\n   * @var string\r\n   */\r\n  protected $redirectTo = \\''/home\\'';\r\n  /**\r\n   * Create a new authentication controller instance.\r\n   *\r\n   * @return void\r\n   */\r\n  public function __construct()\r\n  {\r\n    $this-&gt;middleware(\\''guest\\'', [\\''except\\'' =&gt; \\''logout\\'']);\r\n  }\r\n  /**\r\n   * Get a validator for an incoming registration request.\r\n   *\r\n   * @param  array  $data\r\n   * @return \\\\Illuminate\\\\Contracts\\\\Validation\\\\Validator\r\n   */\r\n  protected function validator(array $data)\r\n  {\r\n    return Validator::make($data, [\r\n      \\''name\\'' =&gt; \\''required|max:255\\'',\r\n      \\''email\\'' =&gt; \\''required|email|max:255|unique:users\\'',\r\n      \\''password\\'' =&gt; \\''required|confirmed|min:6\\'',\r\n      \\''CaptchaCode\\'' =&gt; \\''required|valid_captcha\\'',\r\n    ]);\r\n  }\r\n  /**\r\n   * Create a new user instance after a valid registration.\r\n   *\r\n   * @param  array  $data\r\n   * @return User\r\n   */\r\n  protected function create(array $data)\r\n  {\r\n    return User::create([\r\n      \\''name\\'' =&gt; $data[\\''name\\''],\r\n      \\''email\\'' =&gt; $data[\\''email\\''],\r\n      \\''password\\'' =&gt; bcrypt($data[\\''password\\'']),\r\n    ]);\r\n  }\r\n  /**\r\n   * Handle a login request to the application.\r\n   *\r\n   * @param  \\\\Illuminate\\\\Http\\\\Request  $request\r\n   * @return \\\\Illuminate\\\\Http\\\\Response\r\n   */\r\n  public function login(Request $request)\r\n  {\r\n    $this-&gt;validate($request, [\r\n      $this-&gt;loginUsername() =&gt; \\''required\\'', \\''password\\'' =&gt; \\''required\\'',\r\n      \\''CaptchaCode\\'' =&gt; \\''required|valid_captcha\\'',\r\n    ]);\r\n    // If the class is using the ThrottlesLogins trait, we can automatically throttle\r\n    // the login attempts for this application. We\\''ll key this by the username and\r\n    // the IP address of the client making these requests into this application.\r\n    $throttles = $this-&gt;isUsingThrottlesLoginsTrait();\r\n    if ($throttles &amp;&amp; $this-&gt;hasTooManyLoginAttempts($request)) {\r\n      return $this-&gt;sendLockoutResponse($request);\r\n    }\r\n    $credentials = $this-&gt;getCredentials($request);\r\n    if (Auth::attempt($credentials, $request-&gt;has(\\''remember\\''))) {\r\n      return $this-&gt;handleUserWasAuthenticated($request, $throttles);\r\n    }\r\n    // If the login attempt was unsuccessful we will increment the number of attempts\r\n    // to login and redirect the user back to the login form. Of course, when this\r\n    // user surpasses their maximum number of attempts they will get locked out.\r\n    if ($throttles) {\r\n      $this-&gt;incrementLoginAttempts($request);\r\n    }\r\n    return redirect()-&gt;back()\r\n      -&gt;withInput($request-&gt;only($this-&gt;loginUsername(), \\''remember\\''))\r\n      -&gt;withErrors([\r\n        $this-&gt;loginUsername() =&gt; $this-&gt;getFailedLoginMessage(),\r\n      ]);\r\n  }\r\n}</pre>\r\n<p>\r\n	<br />\r\n<br />\r\n<span style=\\"font-size:14px;\\">控制器：Controller – /app/Http/Controllers/Auth/PasswordController.php</span>\r\n</p>\r\n<pre class=\\"prettyprint lang-php\\">&lt;?php\r\nnamespace App\\\\Http\\\\Controllers\\\\Auth;\r\nuse Illuminate\\\\Http\\\\Request;\r\nuse App\\\\Http\\\\Controllers\\\\Controller;\r\nuse Illuminate\\\\Foundation\\\\Auth\\\\ResetsPasswords;\r\nclass PasswordController extends Controller\r\n{\r\n  /*\r\n  |--------------------------------------------------------------------------\r\n  | Password Reset Controller\r\n  |--------------------------------------------------------------------------\r\n  |\r\n  | This controller is responsible for handling password reset requests\r\n  | and uses a simple trait to include this behavior. You\\''re free to\r\n  | explore this trait and override any methods you wish to tweak.\r\n  |\r\n  */\r\n  use ResetsPasswords;\r\n  /**\r\n   * Create a new password controller instance.\r\n   *\r\n   * @return void\r\n   */\r\n  public function __construct()\r\n  {\r\n    $this-&gt;middleware(\\''guest\\'');\r\n  }\r\n  /**\r\n   * Send a reset link to the given user.\r\n   *\r\n   * @param  \\\\Illuminate\\\\Http\\\\Request  $request\r\n   * @return \\\\Illuminate\\\\Http\\\\Response\r\n   */\r\n  public function sendResetLinkEmail(Request $request)\r\n  {\r\n    $this-&gt;validate($request, [\r\n     \\''email\\'' =&gt; \\''required|email\\'',\r\n     \\''CaptchaCode\\'' =&gt; \\''required|valid_captcha\\''\r\n    ]);\r\n    $response = Password::sendResetLink($request-&gt;only(\\''email\\''), function (Message $message) {\r\n      $message-&gt;subject($this-&gt;getEmailSubject());\r\n    });\r\n    switch ($response) {\r\n      case Password::RESET_LINK_SENT:\r\n        return redirect()-&gt;back()-&gt;with(\\''status\\'', trans($response));\r\n      case Password::INVALID_USER:\r\n        return redirect()-&gt;back()-&gt;withErrors([\\''email\\'' =&gt; trans($response)]);\r\n    }\r\n  }\r\n}</pre>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	<br />\r\n<br />\r\n<br />\r\n</p>'),
(156, 10, '', 1, 0, 0, 0, '', '', '', '<p>\r\n	<span style=\\"font-size:14px;\\">&nbsp;基于laravel5.2的cms开发记录</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">&nbsp;2016-05-30：</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">laravel根目录下.env文件配置到数据库连接信息；</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">config/database.php文件里配置mysql表前缀prefix=\\"la_\\"</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">使用migration创建管理员表la_admin. 命令为：php artisan make:migration create_admin_table --create=admin</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">以上操作在database/migrations目录下生成2016_05_30_090026_create_admin_table.php文件</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">修改迁移文件，up函数中修改和增加表结构信息。down函数中定义回滚操作，通常是删除表，用不着修改。</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">$table-&gt;timestamps();会自动创建create_at和update_at字段。</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">执行迁移，创建数据表：php artisan migrate. 数据库中将新生成la_admin表。php artisan migrate:rollback 可以回滚操作</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">创建模型文件AdminModel.php。php artisan make:model AdminModel</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">生成位置在app目录下。 如果写的是models/AdminModel则生成在app/models目录下，目录不存在则创建。</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">创建登陆页视图admin_login.blade.php</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">创建控制器AdminController.php</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">--index和login函数指向登陆页，dologin方法处理登录请求。</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">配置路由app/http/routes.php</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">数据库的密码字段不是laravel默认的password，需要在模型文件重写getAuthPassword方法，指定自己的字段</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">public function getAuthPassword(){//重写密码字段</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">&nbsp; <span class=\\"Apple-tab-span\\" style=\\"white-space:pre;\\"> </span>return $this-&gt;admin_pass; &nbsp;//把字段设置为admin_pass</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">&nbsp; &nbsp; }</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\"><br type=\\"_moz\\" />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">2016-06-01：</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">发布文章嵌入xheditor编辑器。public/xheditor/</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">得知获取public目录路径的方法是public_path()</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\"><br type=\\"_moz\\" />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\"><br type=\\"_moz\\" />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">&nbsp;</span>\r\n</p>'),
(158, 10, '', 1, 0, 0, 0, '', '', '', '<p>\r\n	<span style=\\"font-size:14px;\\">整理遇到的常见错误 </span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\"> ----定义了控制器，视图和路由后尝试访问页面结果是404找不到页面。 </span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\"> 开启apache的rewrite_module模块。 </span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\"> 参考链接：&nbsp;http://stackoverflow.com/questions/11791375/laravel-routes-not-working&nbsp; </span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\"> ----MethodNotAllowedHttpException in RouteCollection.php line 219 </span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\"> 因为数据是post过来的，但是routes.php写的是get的，所以报错。 </span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\"> ----访问路由未能指向到期望的页面 </span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\"> 路由写法顺序问题，以下路由访问articles/create的时候： </span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\"> Route::get(\\''articles/{id}\\'', \\''ArticlesController@show\\'');<br />\r\nRoute::get(\\''articles/create\\'', \\''ArticlesController@create\\'');<br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\"> {id}通配符匹配到了crate，所以他进入了第一行的show，调整下两行的顺序即可（顺序从特殊到普通，从精确到模糊） </span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\"> ----PDOException in Connector.php line 55:could not find driver </span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">需要开启php的<span style=\\"color:#242729;font-family:Arial, \\''Helvetica Neue\\'', Helvetica, sans-serif;line-height:15.6000003814697px;\\">pdo_mysql扩展：</span><span style=\\"color:#242729;font-family:\\''Arial, Helvetica Neue, Helvetica, sans-serif\\'';\\"><span style=\\"line-height:15.6000003814697px;\\">http://stackoverflow.com/questions/30529583/laravel-error-pdoexception-could-not-find-driver-in-mysql</span></span></span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">&nbsp;----Argument 1 passed to Illuminate\\\\Auth\\\\EloquentUserProvider::validateCredentials() must be an instance of Illuminate\\\\Contracts\\\\Auth\\\\Authenticatable, instance of App\\\\models\\\\AdminModel given, called in D:\\\\wamp\\\\wamp\\\\www\\\\yetpress_cn\\\\laravel\\\\vendor\\\\laravel\\\\framework\\\\src\\\\Illuminate\\\\Auth\\\\SessionGuard.php on line 378 and defined</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">改写model文件，use Illuminate\\\\Foundation\\\\Auth\\\\User as Authenticatable;</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">class AdminModel extends Authenticatable</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">----methodNotAllowed(array(\\''GET\\'', \\''HEAD\\'')) in RouteCollection.php&nbsp;/&nbsp;MethodNotAllowedHttpException in RouteCollection.php line 219</span>\r\n</p>\r\n<p>\r\n	自己遇到的问题是route里面写的是get，而数据是默认的post，就会出现这类错误。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>'),
(159, 10, '', 1, 0, 0, 0, '', '', '', '<p>\r\n	<span style=\\"font-size:14px;\\">&nbsp;假定：user是用户表，username是表中字段，表示用户名；gender也是表中字段，表示性别（0表示保密，1表示男，2表示女）</span> \r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\"> </span> \r\n</p>\r\n<pre class=\\"prettyprint lang-php\\">class User extends \\\\Illuminate\\\\Database\\\\Eloquent\\\\Model {\r\n    protected $table = \\''user\\'';\r\n\r\n    public function getUsername() {\r\n        return $this-&gt;username;\r\n    }\r\n\r\n    public function getFriendlyName() {\r\n        if (1 === $this-&gt;gender) {\r\n            return $this-&gt;username . \\''帅哥\\'';\r\n        } else if (2 === $this-&gt;gender) {\r\n            return $this-&gt;username . \\''美女\\'';\r\n        }\r\n        return $this-&gt;username . \\''神秘人\\'';\r\n    }\r\n}</pre>\r\n<p>\r\n	<span style=\\"font-size:14px;line-height:1.5;\\">以上定义了User类，并定义2个方法。</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;line-height:1.5;\\"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\\"font-size:14px;\\"> </span> \r\n</p>\r\n<pre class=\\"prettyprint lang-php\\">// 获取user表中主键为1的一条记录，并实例化为User类的一个对象\r\n$user = User::find(1);\r\n\r\n// 调用对象的方法\r\necho $user-&gt;getUsername();\r\n\r\n// laravel提供很方便访问数据的方式，所以getUsername这个方法显得有点啰嗦，可以直接访问username\r\necho $user-&gt;username;\r\n\r\n// 当然，如果你有一些特别的数据处理，比如你对原始数据有加工什么的，还是应该在User中定义你自己的方法的\r\necho $user-&gt;getFriendlyName();</pre>\r\n<p>\r\n	<span style=\\"font-size:14px;\\">---------------------------------------------------------------------------</span> \r\n</p>\r\n<span style=\\"font-size:14px;\\">如果User是单纯的数据模型类，或者没有使用ORM</span> \r\n<div style=\\"white-space:nowrap;\\">\r\n<pre class=\\"prettyprint lang-php\\">$user = new User();\r\n$user-&gt;yourMethod();</pre>\r\n<span style=\\"font-size:14px;\\">https://segmentfault.com/q/1010000002567697/a-1020000002568027</span><span style=\\"font-size:14px;\\"></span><br />\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>'),
