E_D("replace into `cn_ecms_news_data_1` values('85','10','','1','0','0','0','','','','<p>&nbsp;<img src=\\\\\"http://files.jb51.net/file_images/article/201306/201306141106221.gif?201351411648\\\\\" alt=\\\\\"\\\\\" /></p>\r\n<p>&nbsp;</p>\r\n<p><img src=\\\\\"http://files.jb51.net/file_images/article/201306/201306141107282.gif?201351411744\\\\\" alt=\\\\\"\\\\\" /></p>\r\n<p>&nbsp;</p>\r\n<p>实例一:</p>\r\n<p>&lt;?php</p>\r\n<p>//使用memcache类来操作</p>\r\n<p>\$mm = new Memcache();</p>\r\n<p>\$mm-&gt;addServer(&quot;192.168.70.114&quot;,11211); //向连接池中添加一个memcache服务器</p>\r\n<p>\$mm-&gt;addServer(&quot;192.168.70.249&quot;,11211);</p>\r\n<p>\$mm-&gt;addServer(&quot;192.168.70.222&quot;,11211);</p>\r\n<p>\$mm-&gt;addServer(&quot;192.168.70.52&quot;,11211);</p>\r\n<p>\$mm-&gt;add(&quot;var_m1&quot;,&quot;zhangsan&quot;,MEMCACHE_COMPRESSED,10000); //添加一个字串</p>\r\n<p>\$mm-&gt;set(&quot;var_m2&quot;,array(&quot;aaa&quot;,&quot;bbbb&quot;,&quot;cccc&quot;),MEMCACHE_COMPRESSED,10000);//添加一个数组</p>\r\n<p>\$mm-&gt;set(&quot;var_m3&quot;,new Stu(),MEMCACHE_COMPRESSED,10000);//添加一个对象</p>\r\n<p>echo \$mm-&gt;get(&quot;var_m1&quot;); //获取信息并输出</p>\r\n<p>echo &quot;&lt;br/&gt;&lt;br/&gt;&quot;;</p>\r\n<p>var_dump(\$mm-&gt;get(&quot;var_m2&quot;));</p>\r\n<p>echo &quot;&lt;br/&gt;&lt;br/&gt;&quot;;</p>\r\n<p>\$ob = \$mm-&gt;get(&quot;var_m3&quot;);</p>\r\n<p>echo \$ob-&gt;getinfo();</p>\r\n<p>echo &quot;&lt;br/&gt;&lt;br/&gt;&quot;;</p>\r\n<p>\$mm-&gt;close();</p>\r\n<p>class Stu{</p>\r\n<p>&nbsp; &nbsp; public \$name=&quot;qq&quot;;</p>\r\n<p>&nbsp; &nbsp; public \$age=20;</p>\r\n<p>&nbsp; &nbsp; public function getinfo(){</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; echo \$this-&gt;name.&quot;:&quot;.\$this-&gt;age;</p>\r\n<p>&nbsp; &nbsp; }</p>\r\n<p>}</p>\r\n<p>?&gt;</p>\r\n<p>实例二:</p>\r\n<p>&lt;?php</p>\r\n<p>//使用memcache类来提高数据的查询速度</p>\r\n<p>\$mm = new Memcache();</p>\r\n<p>\$mm-&gt;addServer(&quot;127.0.0.1&quot;,11211); //向连接池中添加一个memcache服务器</p>\r\n<p>\$mm-&gt;addServer(&quot;192.168.70.249&quot;,11211);</p>\r\n<p>\$mm-&gt;addServer(&quot;192.168.70.222&quot;,11211);</p>\r\n<p>\$mm-&gt;addServer(&quot;192.168.70.52&quot;,11211);</p>\r\n<p>//</p>\r\n<p>\$sql = &quot;select * from stu&quot;;</p>\r\n<p>\$data = \$mm-&gt;get(md5(\$sql)); //尝试以sql语句的md5值作为key从memcache中获取数据</p>\r\n<p>if(!\$data){</p>\r\n<p>&nbsp; &nbsp; echo &quot;执行查询--{\$sql}&lt;br/&gt;&quot;;</p>\r\n<p>&nbsp; &nbsp; //没有数据</p>\r\n<p>&nbsp; &nbsp; \$pdo = new PDO(&quot;mysql:host=localhost;dbname=lamp40&quot;,&quot;root&quot;,&quot;root&quot;);</p>\r\n<p>&nbsp; &nbsp; \$stmt = \$pdo-&gt;query(\$sql);</p>\r\n<p>&nbsp; &nbsp; \$data = \$stmt-&gt;fetchAll(PDO::FETCH_ASSOC);//以关联式数组取出所有数据</p>\r\n<p>&nbsp; &nbsp; //将获取的信息缓存到memcache中</p>\r\n<p>&nbsp; &nbsp; \$mm-&gt;add(md5(\$sql),\$data,MEMCACHE_COMPRESSED,3600);</p>\r\n<p>&nbsp; &nbsp; \$mm-&gt;set(&quot;stu&quot;,md5(\$sql),MEMCACHE_COMPRESSED,3600);</p>\r\n<p>}</p>\r\n<p>//\$mm-&gt;set(md5(\$sql),\$data,MEMCACHE_COMPRESSED,3);</p>\r\n<p>//输出数据</p>\r\n<p>foreach(\$data as \$stu){</p>\r\n<p>&nbsp; &nbsp; echo &quot;{\$stu[\\\\''id\\\\'']}:{\$stu[\\\\''name\\\\'']}&lt;br/&gt;&quot;;</p>\r\n<p>}</p>\r\n<p>\$mm-&gt;close();</p>\r\n<p>?&gt;</p>\r\n<p>&nbsp;</p>');");
